document.addEventListener("DOMContentLoaded",()=>{var e=document.querySelectorAll(".credential-card"),r=window.matchMedia("(prefers-reduced-motion: reduce)").matches;if("IntersectionObserver"in window&&e.length&&!r){e.forEach(e=>e.classList.add("card-hidden"));let r=new IntersectionObserver((e,r)=>{e.forEach(e=>{e.isIntersecting&&(e.target.classList.add("card-visible"),e.target.classList.remove("card-hidden"),r.unobserve(e.target))})},{rootMargin:"0px 0px -10%",threshold:.1});e.forEach(e=>r.observe(e))}});