document.addEventListener("DOMContentLoaded",()=>{var e=document.querySelectorAll(".credential-card");if("IntersectionObserver"in window&&e.length){e.forEach(e=>e.classList.add("card-hidden"));let r=new IntersectionObserver((e,r)=>{e.forEach(e=>{e.isIntersecting&&(e.target.classList.add("card-visible"),e.target.classList.remove("card-hidden"),r.unobserve(e.target))})},{threshold:.1});e.forEach(e=>r.observe(e))}});