document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelectorAll(".credential-card"),r=window.matchMedia("(prefers-reduced-motion: reduce)").matches;if(!("IntersectionObserver"in window)||!e.length||r)return;e.forEach(e=>e.classList.add("card-hidden"));const t=new IntersectionObserver((e,r)=>{e.forEach(e=>{e.isIntersecting&&(e.target.classList.add("card-visible"),e.target.classList.remove("card-hidden"),r.unobserve(e.target))})},{rootMargin:"0px 0px -10%",threshold:.1});e.forEach(e=>t.observe(e))});