function includeHTML(){document.querySelectorAll("[data-include-html]").forEach(t=>{let n=t.getAttribute("data-include-html");n&&fetch(n).then(e=>e.text()).then(e=>{t.innerHTML=e,n.includes("footer.html")&&(e=t.querySelector("#footer-year"))&&(e.textContent=(new Date).getFullYear())}).catch(e=>console.error("Include error:",e))})}document.addEventListener("DOMContentLoaded",includeHTML);