document.addEventListener("DOMContentLoaded",function(){var t=document.getElementById("copy-fingerprint");let n=document.getElementById("pgp-fingerprint");if(t&&n){var o=document.documentElement.lang||"en";let e={success:o.startsWith("fa")?"فینگرپرینت کپی شد! ✅":"Fingerprint copied! ✅",fail:o.startsWith("fa")?"کپی فینگرپرینت با خطا مواجه شد.":"Failed to copy fingerprint.",unsupported:o.startsWith("fa")?"مرورگر شما از کپی کردن پشتیبانی نمی‌کند.":"Your browser does not support copying."};t.addEventListener("click",async()=>{var t=n.textContent.trim();if(navigator.clipboard&&navigator.clipboard.writeText)try{await navigator.clipboard.writeText(t),"function"==typeof createToast&&createToast(e.success)}catch(t){console.error("Failed to copy fingerprint:",t),"function"==typeof createToast&&createToast(e.fail)}else"function"==typeof createToast&&createToast(e.unsupported)})}});